    private void pulsanteAggiungiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pulsanteAggiungiActionPerformed
        String nome = areaTestoAggiungiGiocatori.getText();
        if (!nome.isEmpty())
        {
            if (controller.getGiocatori().size() != 10)
            {
                if (nome.length() <= 20)
                {
                    if (!controller.getGiocatori().contains(nome)) controller.addGiocatore(nome);
                    else new Errore("nome giÃ  presente").setVisible(true);
                }
                else new Errore("nome troppo lungo").setVisible(true);
            }
            else new Errore("massimo giocatori superato").setVisible(true);
            areaTestoAggiungiGiocatori.setText("");
        } else new Errore("nome non inserito").setVisible(true);
    }//GEN-LAST:event_pulsanteAggiungiActionPerformed

    private void pulsanteFattoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pulsanteFattoActionPerformed
        if (controller.getGiocatori().size() > 1) {
            controller.startGioco();
            this.dispose();
        } else new Errore("inserire almeno 2 giocatori").setVisible(true);
    }//GEN-LAST:event_pulsanteFattoActionPerformed

